"use strict";
class Image {
  constructor() {

  }
}

var platform = null;
//detect platform based on the node url argv[0]
if(process.argv[0].startsWith('/')) platform = 'UNX';
else if(process.argv[0].startsWith(/[a-zA-Z]:/)) platform = 'WIN';


console.log("Window location:" + process.cwd() + "/splice.js");
module.exports = {
  console: console,
  location:{href:process.cwd() + '/splice.js'},
  Image: Image,
  __sjs_platform__:platform
};
